import React, { useState } from 'react';
import { Typography, Box, Grid } from '@mui/material';
import '../styles/Category.css';

export default function Category({ category }) {
  const [ segments, setSegments ] = useState([
    {
      text: "that bitch carol at work was so annoying",
      timestamp: 123456
    },
    {
      text: "that bitch carol at work was so annoying",
      timestamp: 123456
    },
  ]);

  return (
    <div>
      <Typography variant="h6">Category chosen: {category}</Typography>
      <Box sx={{
        borderRadius: '10px', // Set the border radius
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Add a shadow
        padding: '20px', // Add some padding for content
        width: 300,
        height: 600,
        backgroundColor: "lightblue",
        overflow: "auto"  
      }}>
        { segments.map((segment) => <Typography>{segment.text}</Typography>) }
      </Box>
    </div>
  )
}
